document.documentElement.outerHTML = `
<!DOCTYPE html>
<html lang="id">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Pemutar Video Dinamis</title>
  <style>
    body {
      font-family: sans-serif;
      padding: 10px;
    }
    iframe {
      width: 100%;
      max-width: 640px;
      height: 360px;
      display: block;
      margin: 0 auto 20px;
    }
    ul {
      list-style: none;
      padding: 0;
    }
    li {
      padding: 10px;
      margin-bottom: 5px;
      background: #f0f0f0;
      cursor: pointer;
      border-radius: 5px;
    }
    li:hover {
      background: #ddd;
    }
  </style>
</head>
<body>

  <h2>Pemutar Video Dinamis</h2>
  <iframe id="videoFrame" src="" frameborder="0" allowfullscreen></iframe>
  <ul id="videoList"></ul>

  <script>
    const videos = [
      {url: "https://fastupload.io/video/embed/0412c1cffcdc7bac/640x360/VID_20240502_124902_982.mp4", title: "Video 1"},
      {url: "https://fastupload.io/video/embed/3236c05f261a512e/640x360/VID_20240502_125021_750.mp4", title: "Video 2"},
      {url: "https://fastupload.io/video/embed/4580725d706730ec/640x360/VID_20240502_125523_158.mp4", title: "Video 3"}
    ];

    const videoList = document.getElementById("videoList");
    const iframe = document.getElementById("videoFrame");

    videos.forEach((video, index) => {
      const li = document.createElement("li");
      li.textContent = video.title;
      li.addEventListener("click", () => {
        iframe.src = video.url;
      });
      videoList.appendChild(li);
    });
  </script>

</body>
</html>
`;
