const jsonUrl = 'https://raw.githubusercontent.com/tipsmenarikid/projects/refs/heads/main/db.json'; // Ganti dengan URL kamu

fetch(jsonUrl)
  .then(response => {
    if (!response.ok) throw new Error("Gagal mengambil data");
    return response.json();
  })
  .then(videos => {
    const videoList = document.getElementById("videoList");
    const iframe = document.getElementById("videoFrame");

    videos.forEach((video, index) => {
      const li = document.createElement("li");
      li.textContent = video.title || `Video ${index + 1}`;
      li.addEventListener("click", () => {
        iframe.src = video.url;
      });
      videoList.appendChild(li);
    });
  })
  .catch(error => {
    console.error("Error:", error);
    document.getElementById("videoList").innerHTML = "<li>Gagal memuat daftar video</li>";
  });
